    rw[POLY_RING] THEN
    simp[poly_add_o_permutes] THEN
    have `ring_sum (poly_ring r (:V)) S p (m o f) = ring_sum (poly_ring r (:V)) S (\s:X m:V->num. p s (m o f)) m:R` [] THEN
    rw[poly_add] THEN
