  have `ring_1 r = ring_mul r c (ring_1(r:R ring))` [] THEN
  have `c = ring_1(r:R ring)` [RING_MUL_RID] THEN
  have `poly_const r c = ring_1 (x_poly(r:R ring))` [poly_1;x_poly_use] THEN
