  have `Cx(&1) IN ZinC` [ZinC_1] THEN
  have `-- Cx(&1) IN ZinC` [neg_in_ZinC] THEN
  have `algebraic_number (-- Cx(&1))` [algebraic_number_ZinC] THEN
