  intro THEN
  have `ring_polynomial r (p:(num->num)->R)` [ring_polynomial_if_subring] THEN
  choose `M:num->num` `support_le r (p:(num->num)->R) M` [support_le_exists] THEN
  specialize[
    `r:R ring`;
