  intro THEN
  have `!s:X. s IN S ==> ring_divides r (a s) (ring_1 r:R)` [RING_UNIT_DIVIDES] THEN
  have `ring_divides r (c:R) (ring_1 r)` [RING_1] THEN
  qed[RING_UNIT_DIVIDES]
