      specialize[
        `r:R ring`;
        `c:R`
      ]INTEGRAL_DOMAIN_MUL_LCANCEL THEN
      qed[RING_MUL;RING_1;ring_divides;ring_prime;UFD_IMP_INTEGRAL_DOMAIN]
    ; pass
    ] THEN
