  subgoal `IMAGE (ring_sum complex_ring (functions S (complex_root p))) (functions (functions S (complex_root p)) (S:complex->bool)) SUBSET algebraic_number` THENL [
    rw[SUBSET;in_image_vw] THEN
    intro THEN
    simp[IN] THEN
